import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [priority, setPriority] = useState("Medium");
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (!taskData) {
      // Reset form when opening the dialog to add a new task
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setPriority("Medium");
      setErrors({});
    } else {
      // Populate form fields when editing an existing task
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setPriority(taskData.priority);
    }
  }, [show, taskData]);

  const validateForm = () => {
    const newErrors = {};
    if (!taskTitle.trim()) newErrors.taskTitle = "Title is required";
    if (!description.trim()) newErrors.description = "Description is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description,
        deadline,
        priority,
      });
      handleClose();
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header
        closeButton
        style={{ backgroundColor: "blue", color: "white" }}
      >
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="taskTitle" className="mb-3">
            <Form.Control
              type="text"
              value={taskTitle}
              onChange={(e) => setTaskTitle(e.target.value)}
              isInvalid={!!errors.taskTitle}
              placeholder="Title"
              style={{ height: "50px" }} // Adjusted height here
            />
            <Form.Control.Feedback type="invalid">
              {errors.taskTitle}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="description" className="mb-3">
            <Form.Control
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.description}
              placeholder="Description"
              style={{ height: "50px" }} // Adjusted height here
            />
            <Form.Control.Feedback type="invalid">
              {errors.description}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="deadline" className="mb-3">
            <DatePicker
              selected={deadline}
              onChange={(date) => setDeadline(date)}
              dateFormat="MM/dd/yyyy"
              className="form-control"
              wrapperClassName="date-picker-wrapper"
            />
          </Form.Group>
          {/* Add priority selection if necessary */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
