import React, { useState } from "react";
import TaskTable from "./TaskTable";
import TaskDialog from "./TaskDialog";
import { Button, Toast, ToastContainer } from "react-bootstrap";

function App() {
  const [showDialog, setShowDialog] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState("");

  const handleShowAddDialog = () => {
    setCurrentTask(null);
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  const handleSaveTask = (task) => {
    if (currentTask) {
      // Update an existing task
      const updatedTasks = tasks.map((t) =>
        t.id === currentTask.id ? { ...t, ...task } : t
      );
      setTasks(updatedTasks);
      setToastMessage("Task updated successfully!");
    } else {
      // Add a new task with unique ID
      const newTask = { ...task, id: Date.now(), isComplete: false };
      setTasks((prevTasks) => [...prevTasks, newTask]);
      setToastMessage("Task added successfully!");
    }
    setShowToast(true);
    setShowDialog(false);
  };

  return (
    <div>
      <div className="container-fluid bg-primary py-3 d-flex justify-content-between align-items-center">
        <h2 className="text-white mx-auto">Frameworks</h2>
        <Button variant="primary" onClick={handleShowAddDialog}>
          Add
        </Button>
      </div>
      <div className="container mt-3">
        <TaskTable tasks={tasks} onEdit={handleShowAddDialog} />
      </div>
      <TaskDialog
        show={showDialog}
        handleClose={handleCloseDialog}
        handleSave={handleSaveTask}
        taskData={currentTask}
      />
      <ToastContainer position="bottom-end" className="p-3">
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={3000}
          autohide
        >
          <Toast.Body>{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
}

export default App;
