import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date()); // Initialize deadline with current date
  const [priority, setPriority] = useState("Medium");
  const [errors, setErrors] = useState({});
  const [radioValue, setRadioValue] = useState("Medium");

  useEffect(() => {
    if (!taskData) {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setPriority("Medium");
      setErrors({});
    } else {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline)); // Assuming taskData.startDate is the deadline
      setPriority(taskData.priority);
      setRadioValue(taskData.priority);
    }
  }, [show, taskData]);

  const validateForm = () => {
    const newErrors = {};
    if (!taskTitle.trim()) newErrors.taskTitle = "Title is required";
    if (!description.trim()) newErrors.description = "Description is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description,
        deadline: deadline.toISOString().slice(0, 10), // Format deadline as MM/DD/YYYY
        priority: radioValue,
      });
      handleClose();
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header
        closeButton
        style={{ backgroundColor: "blue", color: "white" }}
      >
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {/* Title field only for adding a new task */}
          {!taskData && (
            <Form.Group controlId="taskTitle" className="mb-3">
              <Form.Control
                type="text"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                isInvalid={!!errors.taskTitle}
                placeholder="Title"
                style={{ height: "50px" }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.taskTitle}
              </Form.Control.Feedback>
            </Form.Group>
          )}
          <Form.Group controlId="description" className="mb-3">
            <Form.Control
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.description}
              placeholder="Description"
              style={{ height: "50px" }}
            />
            <Form.Control.Feedback type="invalid">
              {errors.description}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="deadline" className="mb-3">
            <div className="d-flex align-items-center mb-2">
              <Form.Label className="me-2">Deadline</Form.Label>
              <Form.Control
                type="date"
                value={deadline.toISOString().slice(0, 10)}
                onChange={(e) => setDeadline(new Date(e.target.value))}
              />
            </div>
          </Form.Group>
          <Form.Group className="mb-3">
            <div className="text-muted">Priority</div>
            <div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="priorityOptions"
                  id="priorityLow"
                  value="Low"
                  checked={radioValue === "Low"}
                  onChange={() => setRadioValue("Low")}
                />
                <label className="form-check-label" htmlFor="priorityLow">
                  Low
                </label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="priorityOptions"
                  id="priorityMedium"
                  value="Medium"
                  checked={radioValue === "Medium"}
                  onChange={() => setRadioValue("Medium")}
                />
                <label className="form-check-label" htmlFor="priorityMedium">
                  Medium
                </label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="priorityOptions"
                  id="priorityHigh"
                  value="High"
                  checked={radioValue === "High"}
                  onChange={() => setRadioValue("High")}
                />
                <label className="form-check-label" htmlFor="priorityHigh">
                  High
                </label>
              </div>
            </div>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
