import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [radioValue, setRadioValue] = useState("Medium");
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (taskData) {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setRadioValue(taskData.priority);
    } else {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setRadioValue("Medium");
      setErrors({});
    }
  }, [taskData, show]);

  const validateForm = () => {
    let newErrors = {};
    if (!taskTitle.trim()) {
      newErrors.taskTitle = "Title is required";
    }
    if (!description.trim()) {
      newErrors.description = "Description is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description: description,
        deadline: deadline.toISOString().slice(0, 10),
        priority: radioValue,
      });
      handleClose();
    }
  };

  const modalDialogStyle = {
    margin: "0 auto",
  };

  return (
    <Modal show={show} onHide={handleClose} centered size="sm">
      <Modal.Header
        closeButton
        style={{ backgroundColor: "blue", color: "white" }}
      >
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {!taskData && (
            <Form.Group controlId="taskTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                isInvalid={!!errors.taskTitle}
              />
              <Form.Control.Feedback type="invalid">
                {errors.taskTitle}
              </Form.Control.Feedback>
            </Form.Group>
          )}
          <Form.Group controlId="description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.description}
            />
            <Form.Control.Feedback type="invalid">
              {errors.description}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="deadline">
            <Form.Label>Deadline</Form.Label>
            <Form.Control
              type="date"
              value={deadline.toISOString().slice(0, 10)}
              onChange={(e) => setDeadline(new Date(e.target.value))}
            />
          </Form.Group>
          <fieldset>
            <Form.Group>
              <Form.Label>Priority</Form.Label>
              <Form.Check
                type="radio"
                label="Low"
                name="priorityOptions"
                id="priorityLow"
                value="Low"
                checked={radioValue === "Low"}
                onChange={() => setRadioValue("Low")}
              />
              <Form.Check
                type="radio"
                label="Medium"
                name="priorityOptions"
                id="priorityMedium"
                value="Medium"
                checked={radioValue === "Medium"}
                onChange={() => setRadioValue("Medium")}
              />
              <Form.Check
                type="radio"
                label="High"
                name="priorityOptions"
                id="priorityHigh"
                value="High"
                checked={radioValue === "High"}
                onChange={() => setRadioValue("High")}
              />
            </Form.Group>
          </fieldset>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
