import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [radioValue, setRadioValue] = useState("Medium");
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (taskData) {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setRadioValue(taskData.priority);
    } else {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setRadioValue("Medium");
      setErrors({});
    }
  }, [taskData, show]);

  const validateForm = () => {
    let newErrors = {};
    if (!taskTitle.trim()) {
      newErrors.taskTitle = "Title is required";
    }
    if (!description.trim()) {
      newErrors.description = "Description is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description: description,
        deadline: deadline.toISOString().slice(0, 10),
        priority: radioValue,
      });
      handleClose();
    }
  };

  const modalDialogStyle = {
    margin: "0 auto",
  };

  return (
    <Modal show={show} onHide={handleClose} centered size="sm">
      <Modal.Header
        closeButton
        style={{ backgroundColor: "blue", color: "white" }}
      >
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {!taskData && (
            <Form.Group controlId="taskTitle" style={{ marginBottom: "25px" }}>
              <Form.Control
                type="text"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                isInvalid={!!errors.taskTitle}
                placeholder="Title"
                style={{ width: "100%", height: "60px" }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.taskTitle}
              </Form.Control.Feedback>
            </Form.Group>
          )}
          <Form.Group controlId="description" style={{ marginBottom: "15px" }}>
            <Form.Control
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.taskTitle}
              placeholder="Description"
              style={{ width: "100%", height: "60px" }}
            />
            <Form.Control.Feedback type="invalid">
              {errors.description}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="deadline" style={{ marginBottom: "25px" }}>
            <Form.Label>Deadline</Form.Label>
            <Form.Control
              type="date"
              value={deadline.toISOString().slice(0, 10)}
              onChange={(e) => setDeadline(new Date(e.target.value))}
              style={{ width: "100%", height: "60px" }}
            />
          </Form.Group>
          <fieldset>
            <Form.Group>
              <Form.Label>Priority</Form.Label>
              <div>
                <Form.Check
                  inline
                  type="radio"
                  label="Low"
                  name="priorityOptions"
                  id="priorityLow"
                  value="Low"
                  checked={radioValue === "Low"}
                  onChange={() => setRadioValue("Low")}
                />
                <Form.Check
                  inline
                  type="radio"
                  label="Medium"
                  name="priorityOptions"
                  id="priorityMedium"
                  value="Medium"
                  checked={radioValue === "Medium"}
                  onChange={() => setRadioValue("Medium")}
                />
                <Form.Check
                  inline
                  type="radio"
                  label="High"
                  name="priorityOptions"
                  id="priorityHigh"
                  value="High"
                  checked={radioValue === "High"}
                  onChange={() => setRadioValue("High")}
                />
              </div>
            </Form.Group>
          </fieldset>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
