import React, { useState, useEffect } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [priority, setPriority] = useState("Low");
  const [errors, setErrors] = useState({});

  // Reset form when taskData changes
  useEffect(() => {
    if (taskData) {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setPriority(taskData.priority);
    } else {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setPriority("Low");
    }
    setErrors({});
  }, [taskData]);

  const validateForm = () => {
    const newErrors = {};
    if (!taskTitle) newErrors.taskTitle = "Title is required";
    if (!description) newErrors.description = "Description is required";
    if (!deadline) newErrors.deadline = "Deadline is required";
    if (!priority) newErrors.priority = "Priority is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description,
        deadline,
        priority,
      });
      handleClose();
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>{/* ... rest of your form */}</Form>
      </Modal.Body>
      {/* ... rest of your modal */}
    </Modal>
  );
};

export default TaskDialog;
