import React, { useState } from "react";
import TaskTable from "./TaskTable"; // Adjust the path based on your file structure
import TaskDialog from "./TaskDialog"; // Ensure you have this component created
import { Toast, ToastContainer } from "react-bootstrap";

function App() {
  const [showDialog, setShowDialog] = useState(false);
  const [tasks, setTasks] = useState([]); // Initialize task list as empty
  const [currentTask, setCurrentTask] = useState(null); // For edit functionality

  // Function to open the dialog to add a new task
  const handleShowAddDialog = () => {
    setCurrentTask(null); // No current task when adding a new one
    setShowDialog(true);
  };

  // Function to open the dialog to edit an existing task
  const handleShowEditDialog = (task) => {
    setCurrentTask(task); // Set the current task to be edited
    setShowDialog(true);
  };

  // Function to close the dialog
  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  // Function to save a new task or update an existing one
  const handleSaveTask = (task) => {
    if (currentTask) {
      // Update an existing task
      const updatedTasks = tasks.map((t) =>
        t.id === currentTask.id ? { ...task, id: currentTask.id } : t
      );
      setTasks(updatedTasks);
    } else {
      // Add a new task (with a simple ID generation for example purposes)
      const newTask = { ...task, id: Date.now() };
      setTasks([...tasks, newTask]);
    }
    setShowDialog(false); // Close the dialog after saving
  };

  return (
    <div className="container mt-5">
      {/* Button to add a new task */}
      <TaskTable tasks={tasks} onEdit={handleShowEditDialog} />
      <TaskDialog
        show={showDialog}
        handleClose={handleCloseDialog}
        handleSave={handleSaveTask}
        taskData={currentTask}
      />
    </div>
  );
}

export default App;
