import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  // Define state variables
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [radioValue, setRadioValue] = useState("Medium");
  const [errors, setErrors] = useState({});

  // Effect for setting the initial state when the component mounts or the props change
  useEffect(() => {
    if (!taskData) {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setRadioValue("Medium");
      setErrors({});
    } else {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setRadioValue(taskData.priority);
    }
  }, [show, taskData]);

  // Function for validating the form
  const validateForm = () => {
    const newErrors = {};
    if (!taskTitle.trim()) newErrors.taskTitle = "Title is required";
    if (!description.trim()) newErrors.description = "Description is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Function for handling the submit action
  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description,
        deadline: deadline.toISOString().slice(0, 10),
        priority: radioValue,
      });
      handleClose();
    }
  };

  // Inline styles for a thinner modal
  const thinModalStyle = {
    maxWidth: "300px", // Adjust width as needed
    margin: "0 auto",
  };

  return (
    <Modal show={show} onHide={handleClose} centered style={thinModalStyle}>
      <Modal.Header
        closeButton
        style={{ backgroundColor: "blue", color: "white", padding: "0.5rem" }}
      >
        <Modal.Title>{taskData ? "Edit Task" : "Add Task"}</Modal.Title>
      </Modal.Header>
      <Modal.Body style={{ padding: "0.5rem" }}>
        <Form>
          {/* Title field */}
          {!taskData && (
            <Form.Group controlId="taskTitle" className="mb-3">
              <Form.Control
                type="text"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                isInvalid={!!errors.taskTitle}
                placeholder="Title"
                style={{ marginBottom: "0.5rem" }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.taskTitle}
              </Form.Control.Feedback>
            </Form.Group>
          )}
          {/* Description field */}
          <Form.Group controlId="description" className="mb-3">
            <Form.Control
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.description}
              placeholder="Description"
              style={{ marginBottom: "0.5rem" }}
            />
            <Form.Control.Feedback type="invalid">
              {errors.description}
            </Form.Control.Feedback>
          </Form.Group>
          {/* Deadline field */}
          <Form.Group controlId="deadline" className="mb-3">
            <Form.Label>Deadline</Form.Label>
            <Form.Control
              type="date"
              value={deadline.toISOString().slice(0, 10)}
              onChange={(e) => setDeadline(new Date(e.target.value))}
              style={{ marginBottom: "0.5rem" }}
            />
          </Form.Group>
          {/* Priority field */}
          <Form.Group className="mb-3">
            <Form.Label>Priority</Form.Label>
            <div>
              <Form.Check
                inline
                label="Low"
                type="radio"
                name="priorityOptions"
                id="priorityLow"
                value="Low"
                checked={radioValue === "Low"}
                onChange={() => setRadioValue("Low")}
              />
              <Form.Check
                inline
                label="Medium"
                type="radio"
                name="priorityOptions"
                id="priorityMedium"
                value="Medium"
                checked={radioValue === "Medium"}
                onChange={() => setRadioValue("Medium")}
              />
              <Form.Check
                inline
                label="High"
                type="radio"
                name="priorityOptions"
                id="priorityHigh"
                value="High"
                checked={radioValue === "High"}
                onChange={() => setRadioValue("High")}
              />
            </div>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer style={{ padding: "0.5rem" }}>
        <Button
          variant="secondary"
          onClick={handleClose}
          style={{ marginRight: "0.5rem" }}
        >
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
