import React, { useState } from "react";
import TaskDialog from "./TaskDialog";
// ... (rest of your imports)

function TaskTable() {
  const [showDialog, setShowDialog] = useState(false);

  const handleShowDialog = () => {
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  const handleSave = (newTask) => {
    // Logic to save the new task
    setShowDialog(false);
  };

  return (
    <div className="container mt-5">
      {/* Header with blue border */}
      <div
        className="row mb-3 align-items-center"
        style={{ borderBottom: "2px solid #007BFF", paddingBottom: "10px" }}
      >
        <div className="col text-center">
          <h2 className="mb-0">Frameworks</h2>
        </div>
        <div className="col text-right">
          <button className="btn btn-primary" onClick={handleShowDialog}>
            Add
          </button>
        </div>
      </div>

      <TaskDialog
        show={showDialog}
        handleClose={handleCloseDialog}
        handleSave={handleSave}
        taskData={null} // Pass existing task data if editing or null if adding
      />
      {/* Table and other content below */}
      <table className="table table-bordered">
        <thead className="thead-dark">
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Deadline</th>
            <th>Priority</th>
            <th>Is Complete</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{/* Format and display deadline */}</td>
              <td>{task.priority}</td>
              {/* Implement editing and deletion if necessary */}
            </tr>
          ))}
        </tbody>
      </table>

      <TaskDialog />
    </div>
  );
}

export default TaskTable;
