import React from "react";
import { Button } from "react-bootstrap";

function formatDate(dateString) {
  const date = new Date(dateString);
  const formattedDate = `${
    date.getMonth() + 1
  }/${date.getDate()}/${date.getFullYear()}`;
  return formattedDate;
}

function TaskTable({ tasks, onEdit, onDelete, onCompleteChange }) {
  // Accept onCompleteChange as a prop
  return (
    <div className="container mt-5">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Deadline</th>
            <th>Priority</th>
            <th>Is Complete</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{formatDate(task.deadline)}</td>
              <td>{task.priority}</td>
              <td>
                <input
                  type="checkbox"
                  checked={task.isComplete}
                  onChange={() => onCompleteChange(task.id, !task.isComplete)} // Correctly use the onCompleteChange prop
                />
              </td>
              <td>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    gap: "0",
                  }}
                >
                  {!task.isComplete && (
                    <Button
                      variant="primary"
                      onClick={() => onEdit(task)}
                      style={{ width: "50%" }} // This button will be hidden when task is complete
                    >
                      Update
                    </Button>
                  )}
                  <Button
                    variant="danger"
                    onClick={() => onDelete(task.id)}
                    style={{ width: "50%" }} // The width remains the same, ensuring the Delete button doesn't stretch or change position
                  >
                    Delete
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TaskTable;
