import React, { useState } from "react";
import TaskTable from "./TaskTable";
import TaskDialog from "./TaskDialog";
import { Toast, ToastContainer } from "react-bootstrap";

function App() {
  const [showDialog, setShowDialog] = useState(false);
  const [tasks, setTasks] = useState([]); // Initialize task list as empty
  const [currentTask, setCurrentTask] = useState(null); // For edit functionality
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState("");

  const handleShowDialog = (task = null) => {
    setCurrentTask(task); // If task is null, it's a new task, otherwise it's an edit
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  const handleSaveTask = (task) => {
    if (currentTask) {
      // Update an existing task
      const updatedTasks = tasks.map((t) =>
        t.id === currentTask.id ? { ...task, id: currentTask.id } : t
      );
      setTasks(updatedTasks);
      setToastMessage("Task updated successfully!");
    } else {
      // Add a new task (with a simple ID generation for example purposes)
      const newTask = { ...task, id: Date.now() };
      setTasks((prevTasks) => [...prevTasks, newTask]);
      setToastMessage("Task added successfully!");
    }
    setShowToast(true);
    setShowDialog(false);
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
    setToastMessage("Task deleted successfully!");
    setShowToast(true);
  };

  return (
    <div className="container mt-5">
      <TaskTable
        tasks={tasks}
        onEdit={handleShowDialog}
        onDelete={handleDeleteTask}
      />
      {showDialog && (
        <TaskDialog
          show={showDialog}
          handleClose={handleCloseDialog}
          handleSave={handleSaveTask}
          taskData={currentTask}
        />
      )}
      <ToastContainer position="bottom-end" className="p-3">
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={3000}
          autohide
        >
          <Toast.Body>{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
}

export default App;
