import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState(new Date());
  const [radioValue, setRadioValue] = useState("Medium");
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (taskData) {
      setTaskTitle(taskData.title);
      setDescription(taskData.description);
      setDeadline(new Date(taskData.deadline));
      setRadioValue(taskData.priority);
    } else {
      setTaskTitle("");
      setDescription("");
      setDeadline(new Date());
      setRadioValue("Medium");
      setErrors({});
    }
  }, [taskData, show]);

  const validateForm = () => {
    let newErrors = {};
    if (!taskTitle.trim()) {
      newErrors.taskTitle = "Title is required";
    }
    if (!description.trim()) {
      newErrors.description = "Description is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description: description,
        deadline: deadline.toISOString().slice(0, 10),
        priority: radioValue,
      });
      handleClose();
    }
  };

  // Remove unnecessary style object since it's not being used
  // and inline styles will be defined directly within components

  return (
    <Modal
      show={show}
      onHide={handleClose}
      centered
      size="lg"
      style={{ fontFamily: "Arial, sans-serif" }}
    >
      {" "}
      {/* Adjust the size and font */}
      <Modal.Header
        closeButton
        style={{ backgroundColor: "#f0f0f0", borderBottom: "none" }}
      >
        {" "}
        {/* Match the header background color to the screenshot */}
        <Modal.Title style={{ fontWeight: "bold", color: "#000" }}>
          {taskData ? "Edit Task" : "Add Task"}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body style={{ padding: "20px" }}>
        <Form>
          <Form.Group controlId="taskTitle" style={{ marginBottom: "15px" }}>
            <Form.Control
              type="text"
              value={taskTitle}
              onChange={(e) => setTaskTitle(e.target.value)}
              isInvalid={!!errors.taskTitle}
              placeholder="Title"
              style={{
                borderColor: errors.taskTitle ? "#dc3545" : "#ced4da",
                height: "38px",
                marginBottom: "10px",
              }}
            />
            <Form.Text
              style={{
                color: "#dc3545",
                display: errors.taskTitle ? "block" : "none",
              }}
            >
              {errors.taskTitle}
            </Form.Text>
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Control
              as="textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              isInvalid={!!errors.description}
              placeholder="Description"
              style={{
                borderColor: errors.description ? "#dc3545" : "#ced4da",
                height: "100px",
                marginBottom: "10px",
              }}
            />
            <Form.Text
              style={{
                color: "#dc3545",
                display: errors.description ? "block" : "none",
              }}
            >
              {errors.description}
            </Form.Text>
          </Form.Group>
          <Form.Group controlId="deadline" style={{ marginBottom: "15px" }}>
            <Form.Label
              style={{
                fontWeight: "bold",
                color: "#000",
                fontSize: "0.875rem",
                marginBottom: "5px",
              }}
            >
              Deadline
            </Form.Label>
            <Form.Control
              type="date"
              value={deadline.toISOString().slice(0, 10)}
              onChange={(e) => setDeadline(new Date(e.target.value))}
              style={{ borderColor: "#ced4da", height: "38px" }}
            />
          </Form.Group>
          <Form.Group style={{ marginBottom: "15px" }}>
            <Form.Label
              style={{
                fontWeight: "bold",
                color: "#000",
                fontSize: "0.875rem",
                marginBottom: "5px",
              }}
            >
              Priority
            </Form.Label>
            <div key={`inline-radio`} className="mb-3">
              <Form.Check
                inline
                label="Low"
                name="priorityOptions"
                type="radio"
                id={`inline-radio-1`}
                value="Low"
                checked={radioValue === "Low"}
                onChange={() => setRadioValue("Low")}
                style={{
                  fontWeight: radioValue === "Low" ? "bold" : "normal",
                  color: "#000",
                }}
              />
              <Form.Check
                inline
                label="Med"
                name="priorityOptions"
                type="radio"
                id={`inline-radio-2`}
                value="Medium"
                checked={radioValue === "Medium"}
                onChange={() => setRadioValue("Medium")}
                style={{
                  fontWeight: radioValue === "Medium" ? "bold" : "normal",
                  color: "#000",
                }}
              />
              <Form.Check
                inline
                label="High"
                name="priorityOptions"
                type="radio"
                id={`inline-radio-3`}
                value="High"
                checked={radioValue === "High"}
                onChange={() => setRadioValue("High")}
                style={{
                  fontWeight: radioValue === "High" ? "bold" : "normal",
                  color: "#000",
                }}
              />
            </div>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer style={{ borderTop: "none", paddingTop: 0 }}>
        <Button
          variant="outline-secondary"
          onClick={handleClose}
          style={{ fontWeight: "bold" }}
        >
          Cancel
        </Button>
        <Button
          variant="outline-primary"
          onClick={handleSubmit}
          style={{ fontWeight: "bold" }}
        >
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
