import React, { useState } from "react";
import TaskTable from "./TaskTable";
import TaskDialog from "./TaskDialog";
import { Toast, ToastContainer } from "react-bootstrap";

function App() {
  const [showDialog, setShowDialog] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState("");

  const handleShowDialog = (task = null) => {
    setCurrentTask(task);
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  const handleSaveTask = (task) => {
    if (currentTask) {
      const updatedTasks = tasks.map((t) =>
        t.id === currentTask.id ? { ...task, id: currentTask.id } : t
      );
      setTasks(updatedTasks);
      setToastMessage("Task updated successfully!");
    } else {
      const newTask = { ...task, id: Date.now(), isComplete: false };
      setTasks((prevTasks) => [...prevTasks, newTask]);
      setToastMessage("Task added successfully!");
    }
    setShowToast(true);
    setShowDialog(false);
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
    setToastMessage("Task deleted successfully!");
    setShowToast(true);
  };

  return (
    <div className="container mt-5">
      <header
        className="d-flex justify-content-between align-items-center mb-3"
        style={{ borderBottom: "2px solid #007BFF", paddingBottom: "10px" }}
      >
        <h2>FRAMEWORKS</h2>
        <button className="btn btn-primary" onClick={() => handleShowDialog()}>
          Add
        </button>
      </header>
      <TaskTable
        tasks={tasks}
        onEdit={handleShowDialog}
        onDelete={handleDeleteTask}
      />
      {showDialog && (
        <TaskDialog
          show={showDialog}
          handleClose={handleCloseDialog}
          handleSave={handleSaveTask}
          taskData={currentTask}
        />
      )}
      <ToastContainer position="bottom-end" className="p-3">
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={3000}
          autohide
        >
          <Toast.Body>{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
}

export default App;
