import React, { useState } from "react";
import TaskTable from "./TaskTable";
import TaskDialog from "./TaskDialog";
import { Button } from "react-bootstrap";

function App() {
  const [showDialog, setShowDialog] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);

  const handleShowAddDialog = () => {
    setCurrentTask(null);
    setShowDialog(true);
  };

  const handleCloseDialog = () => {
    setShowDialog(false);
  };

  const handleSaveTask = (task) => {
    if (currentTask) {
      // Update an existing task
      const updatedTasks = tasks.map((t) =>
        t.id === currentTask.id ? { ...t, ...task } : t
      );
      setTasks(updatedTasks);
    } else {
      // Add a new task with unique ID
      const newTask = { ...task, id: Date.now() };
      setTasks((prevTasks) => [...prevTasks, newTask]);
    }
    setShowDialog(false);
  };

  return (
    <div className="container mt-5">
      <div className="bg-primary d-flex justify-content-between align-items-center mb-3 px-3 py-3">
        <h2 className="text-white mb-0 text-center flex-grow-1">Frameworks</h2>
        <Button variant="primary" onClick={handleShowAddDialog}>
          Add
        </Button>
      </div>
      <TaskTable
        tasks={tasks}
        onEdit={(task) => {
          setCurrentTask(task);
          setShowDialog(true);
        }}
      />
      <TaskDialog
        show={showDialog}
        handleClose={handleCloseDialog}
        handleSave={handleSaveTask}
        taskData={currentTask}
      />
    </div>
  );
}

export default App;
