import React, { useState } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const TaskDialog = ({ show, handleClose, handleSave, taskData }) => {
  const [taskTitle, setTaskTitle] = useState(taskData ? taskData.title : "");
  const [description, setDescription] = useState(
    taskData ? taskData.description : ""
  );
  const [deadline, setDeadline] = useState(
    taskData ? new Date(taskData.deadline) : null
  );
  const [priority, setPriority] = useState(taskData ? taskData.priority : "");
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!taskTitle) newErrors.taskTitle = "Title is required";
    if (!description) newErrors.description = "Description is required";
    if (!deadline) newErrors.deadline = "Deadline is required";
    if (!priority) newErrors.priority = "Priority is required";
    // Add more validation as needed
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      handleSave({
        title: taskTitle,
        description,
        deadline,
        priority,
      });
      handleClose(); // Close modal on successful save
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton></Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group as={Row} controlId="taskTitle">
            <Form.Label column sm="3">
              Title
            </Form.Label>
            <Col sm="9">
              <Form.Control
                type="text"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
                isInvalid={!!errors.taskTitle}
              />
              <Form.Control.Feedback type="invalid">
                {errors.taskTitle}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId="description">
            <Form.Label column sm="3">
              Description
            </Form.Label>
            <Col sm="9">
              <Form.Control
                as="textarea"
                rows={3}
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                isInvalid={!!errors.description}
              />
              <Form.Control.Feedback type="invalid">
                {errors.description}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId="deadline">
            <Form.Label column sm="3">
              Deadline
            </Form.Label>
            <Col sm="9">
              <DatePicker
                selected={deadline}
                onChange={(date) => setDeadline(date)}
                dateFormat="MM/dd/yyyy"
                className={`form-control ${
                  errors.deadline ? "is-invalid" : ""
                }`}
              />
              <div className="invalid-feedback">{errors.deadline}</div>
            </Col>
          </Form.Group>
          <fieldset>
            <Form.Group as={Row}>
              <Form.Label as="legend" column sm={3}>
                Priority
              </Form.Label>
              <Col sm={9}>
                {["Low", "Medium", "High"].map((p) => (
                  <Form.Check
                    key={p}
                    type="radio"
                    label={p}
                    name="priorityGroup"
                    id={`priority-${p}`}
                    value={p}
                    checked={priority === p}
                    onChange={(e) => setPriority(e.target.value)}
                    isInvalid={!!errors.priority}
                  />
                ))}
                <Form.Control.Feedback
                  type="invalid"
                  style={{ display: priority ? "none" : "block" }}
                >
                  {errors.priority}
                </Form.Control.Feedback>
              </Col>
            </Form.Group>
          </fieldset>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {taskData ? "Update" : "Add"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskDialog;
