// TaskTable.js
import React from "react";
import { Button } from "react-bootstrap";

function formatDate(dateString) {
  const date = new Date(dateString);
  const formattedDate = `${
    date.getMonth() + 1
  }/${date.getDate()}/${date.getFullYear()}`;
  return formattedDate;
}

function TaskTable({ tasks, onEdit, onDelete }) {
  return (
    <div className="container mt-5">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Deadline</th>
            <th>Priority</th>
            <th>Is Complete</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{formatDate(task.deadline)}</td>{" "}
              {/* Display formatted deadline */}
              <td>{task.priority}</td>
              <td>
                <input
                  type="checkbox"
                  checked={task.isComplete}
                  onChange={() => {
                    // Handle change
                  }}
                />
              </td>
              <td>
                {/* Conditionally render the "Update" button */}
                {!task.isComplete && (
                  <Button
                    variant="primary"
                    style={{ backgroundColor: "blue", color: "white" }}
                    onClick={() => onEdit(task)}
                    className="me-2"
                  >
                    Update
                  </Button>
                )}
                {/* Add Delete button */}
                <Button
                  variant="danger"
                  onClick={() => onDelete(task.id)}
                  className="me-2"
                  style={{ width: "100%" }}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TaskTable;
